import numpy as np

# Create an array
arr = np.array([1, 2, 3, 4, 5])

# 1. Type of array
print(f"Type of array: {type(arr)}")

# 2. Axes of array
print(f"Axes of array: {arr.ndim}")

# 3. Shape of array
print(f"Shape of array: {arr.shape}")

# 4. Type of elements in array
print(f"Type of elements in array: {arr.dtype}")